let burger = document.getElementsByClassName("burger")[0];
let menu = document.getElementsByClassName("menu")[0];
let body = document.getElementsByTagName("body")[0];
let xButton = document.getElementsByClassName("x-button")[0];
let menu_xButton = document.getElementsByClassName("menu-x-button")[0];
let toggleEvent = false;
let body_active;
let litsts = document.querySelector('.litsts');
let litsts_all_li;
let litst_1 = litsts.querySelector('.list');
let litst_2 = litsts.querySelector('.media');


burger.addEventListener("click", showMenu);
window.addEventListener("resize", resize);

function resize() {
    menu.style.height = window.innerHeight + "px";
}

function showMenu() {
    bodyScrollLock.disableBodyScroll(litsts);
    menu.style.height = window.innerHeight + "px";
    menu.classList.toggle("active");
    xButton.classList.toggle("active");
    menu_xButton.classList.toggle("active");
    body.classList.toggle("active");
    body_active = document.querySelector("body.active");
    litsts.classList.toggle("active");
    toggleListener();
    setTimeout(() => {
        litsts.classList.toggle("base-transition");
    }, 500);
}

function hideMenu() {
    if (!menu.contains(event.target) && !burger.contains(event.target) || menu_xButton.contains(event.target)) {
        bodyScrollLock.enableBodyScroll(litsts);
        menu_xButton.classList.toggle("active");
        menu.classList.toggle("active");
        xButton.classList.toggle("active");
        body.classList.toggle("active");
        toggleListener();
        litsts.classList.toggle("active");
        litsts.classList.toggle("base-transition");
    }
}

function toggleListener() {
    if (!toggleEvent) {
        menu_xButton.addEventListener("click", hideMenu);
        document.addEventListener("click", hideMenu);
        toggleEvent = true;
    } else {
        menu_xButton.removeEventListener("click", hideMenu);
        document.removeEventListener("click", hideMenu);
        toggleEvent = false;
    }

}

let header = document.getElementsByClassName("header")[0];
let top_menu = header.getElementsByClassName("top__menu")[0];
let a_menu = top_menu.getElementsByClassName("menu")[0];
let links = a_menu.getElementsByTagName("a");
let media = top_menu.getElementsByClassName("media")[0];
let a_media = media.getElementsByTagName("a");
let menu_burger = document.getElementsByClassName("menu-burger")[0];
let burger_menu = menu_burger.getElementsByClassName('menu')[0];
let burger_list = document.getElementsByClassName("list")[0];
let burger_lists = burger_menu.getElementsByClassName("litsts")[0];





window.addEventListener("resize", function () {
    myFunction();
});

myFunction();

function myFunction() {
    if (window.matchMedia("(max-width: 600px)").matches) {
        moveTopMenu();
        moveA_media();
    } else {
        removeTopMenu();
        removeA_media();
    }
}


function moveTopMenu() {
    if (burger_list.querySelector("#moved_top_menu") === null) {
        for (i = 0; i < links.length; i++) {
            var li = document.createElement("li");
            var a = document.createElement("a");
            li.innerHTML = links[i].innerHTML;
            a.appendChild(li);
            a.setAttribute("href", "#");
            a.setAttribute("id", "moved_top_menu");
            burger_list.appendChild(a);
        }
    }
}

function removeTopMenu() {
    if (burger_list.querySelector("#moved_top_menu") != null) {
        for (let i = 0; i < links.length; i++) {
            const element = burger_list.querySelector("#moved_top_menu");
            element.parentNode.removeChild(element);
        }
    }
}

function moveA_media() {
    if (burger_lists.querySelector(".media") === null) {
        var ul = document.createElement("ul");
        ul.setAttribute("class", "media");
        for (i = 0; i < a_media.length; i++) {
            var li = document.createElement("li");
            var a = document.createElement("a");
            li.innerHTML = a_media[i].innerHTML;
            a.setAttribute("href", "#");
            a.setAttribute("id", "moved_a_media");
            a.appendChild(li);
            ul.appendChild(a);
        }
        burger_lists.appendChild(ul);
    }


}

function removeA_media() {
    if (burger_lists.querySelector(".media") != null) {
        const element = burger_lists.querySelector(".media");
        element.parentNode.removeChild(element);
    }
}
let first_block = document.querySelector(".first-block");

const onIntersection1 = (entries) => {
    for (const entry of entries) {
        if (entry.isIntersecting) {
            // console.log(entry);
            first_block.classList.toggle("anim_on");
            setTimeout(() => {
                first_block.classList.toggle("anim_off");
            }, 1000);
        }
        if (!entry.isIntersecting) {
            // console.log(entry);
            first_block.classList.remove("anim_on");
            first_block.classList.remove("anim_off");
        }
    }
};

